import React, { Component } from "react";
import './navbar.scss'
import { Link, withRouter } from "react-router-dom";
// import { connect } from "react-redux";
// import { logoutUser } from "../../redux/actions";
export default class Navbar extends Component{
    constructor(props){
        super();
        this.state={
            onShow:[false,false,false,false,true],
        };
    }


    ClickHandle(prop,e){
        console.log(prop);
        let arr = [false,false,false,false,false];
        arr[prop] = true;
        console.log(arr);
        this.setState={
            onshow:arr,
        }

    }
    render(){
        let arr=this.state.onShow;
        console.log(this.state.onShow[0])
        console.log("------")
        console.log(arr)
        return(
        <div className="pf__title">
            <div className="pf__title--name">
                <p>Jubi  </p> 
                <p>  Chen</p>
            </div>
            <ul>
            {/* style="color:#86858A" */}    
                <li>{this.state.onShow}</li>
                <li ><Link to="/contact" className={this.state.onShow[0]?"pf__title--link-active":"pf__title--link"} onClick={this.ClickHandle.bind(this,0)}>Contact</Link></li>
                <li ><Link to="/blog" className={arr[1]?"pf__title--link-active":"pf__title--link"} onClick={this.ClickHandle.bind(this,1)}>Blog</Link></li>
                <li ><Link to="/services"className={arr[2]?"pf__title--link-active":"pf__title--link"} onClick={this.ClickHandle.bind(this,2)}>Services</Link></li>
                <li ><Link to="/resume" className={arr[3]?"pf__title--link-active":"pf__title--link"} onClick={this.ClickHandle.bind(this,3)}>Resume</Link></li>
                <li ><Link to="/home" className={arr[4]?"pf__title--link-active":"pf__title--link"} onClick={this.ClickHandle.bind(this,4)}>Home</Link></li>
            </ul>
     </div>)

    }
}
