                    onTabChange={setSelectedTab}
                    generateMarkdownPreview={markdown =>
                    Promise.resolve(converter.makeHtml(markdown))
                    }
                    loadSuggestions={loadSuggestions}
                    childProps={{
                    writeButton: {
                        tabIndex: -1
                    }
                    }}
                />
        </div>
        </div>
