const initialState = {
    authShow: false
}
const authShow = (state = initialState,action)=>{
    switch (action.type) {
        case 'CHANGE_AUTH_SHOW':
            return{
                ...state,
                authShow:action.authShow
            };
        default:
            return state;

    }
}
export default authShow;
