import React, { Component } from "react";
//import { Link, withRouter } from "react-router-dom";
import './blog.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/fontawesome-free-solid'
export default class Blog extends Component{
    constructor(props){
        super();
        this.state={
            page:1,
            size:4,
            articles:[]
        }
    }
    async componentDidMount(){
        const apiUrl='https://profile-article-api.herokuapp.com';
        const link = `${apiUrl}/article/${this.state.page}/${this.state.size}`;
        console.log(link)
        const res = fetch(link,{ 
            method:'get',
            // mode: 'no-cors',
        })
        res.then(res=>res.json()).then(data=>{
            this.setState({articles:data})
            this.state.articles.map((item,index)=>{
                console.log(item.post_date)
                let pdate,date;
                pdate=item.post_date.split(' ')[0]
                date=pdate.split('-')
                console.log(date[1])
                console.log(date[2])
            })
            // console.log(this.state.articles)
        })
        // r.then(data=>console.log(data))
        // for(const item of data){
        //     console.log(item)
        // }
        // console.log(res)
        // console.log(r)
        // console.log(res.body)


    }
    render(){
        console.log(this.state.articles)
        return(
        <div className="pf__blog">
            <section className="pf__blog--fp" >
                <h1>Blog</h1>
                <div className="pf__blog--fp-search"><input></input><FontAwesomeIcon icon={faSearch} /></div>
            </section>
            <section className="pf__blog--sp" >
                {this.state.articles.map((item,index)=>

                    (<div className="pf__blog--sp--article">
                        <div className="pf__blog--sp--article-bg"> 
                            <div className="pf__blog--sp--article-bg-date"><h3>{item.post_date.split(' ')[0].split('-')[2]}<span>{(new Date(item.post_date)).toDateString.split(' ')}</span></h3></div>
                            <a onClick={()=>{
                                this.props.history.push(`/article/${item._id}`);
                            }}>
                                <img src="https://www.edwardhopper.net/images/paintings/rooms-by-the-sea.jpg"></img> 
                                <div className="mask">📄</div>
                            </a>

                        </div>
                        <div className="pf__blog--sp--article-info">
                            <p>{item.categroy}</p>
                            <h4>{item.title}</h4>
                        </div>
                    </div>)
                )}
                {/* {this.state.articles.map((item,index)=>{
                    <>
                    <div className="pf__blog--sp--article">
                        <div className="pf__blog--sp--article-bg"> 
                            <div className="pf__blog--sp--article-bg-date"><h3>6 <span>Dec</span></h3></div>
                            <a onClick={()=>{
                                this.props.history.push(`/article/${item._id}`);
                            }}>
                                <img src="https://www.edwardhopper.net/images/paintings/rooms-by-the-sea.jpg"></img> 
                                <div className="mask">📄</div>
                            </a>

                        </div>
                        <div className="pf__blog--sp--article-info">
                            <p>{item.categroy}</p>
                            <h4>{item.title}</h4>
                        </div>
                    </div>
                    </>
                })} */}
                <div className="pf__blog--sp--article">
                    <div className="pf__blog--sp--article-bg"> 
                        <div className="pf__blog--sp--article-bg-date"><h3>6 <span>Dec</span></h3></div>
                        <a onClick={()=>{
                            this.props.history.push('/article/'+'1');
                        }}>
                            <img src="https://www.edwardhopper.net/images/paintings/rooms-by-the-sea.jpg"></img> 
                            <div className="mask">📄</div>
                        </a>

                    </div>
                    <div className="pf__blog--sp--article-info">
                        <p>categroy</p>
                        <h4>Bootstrap is the Most Popular Framework</h4>
                    </div>
                </div>


            </section>
        </div>)
    }
}
